mapper.insert:
INSERT INTO
    board_tbl (
         name
       , content
       , view_qty
       , like_qty
       , create_dt
       , create_id
       , del_flag
)
VALUES (
        #{name}
       , #{content}
       , #{viewQty}
       , #{likeQty}
       , #{createDt}
       , #{createId}
       , 'N'
)

mapper.update:
UPDATE board_tbl
    SET name = #{name}
    , content = #{content}
    , view_qty = #{viewQty}
    , like_qty = #{likeQty}
    , update_dt = #{updateDt}
    , update_id = #{updateId}
WHERE id = #{id}

UPDATE board_tbl
SET delete_dt = #{deleteDt}
  , delete_id = #{deleteId}
  , del_flag = 'Y'
WHERE id = #{id}
