mapper.update:
UPDATE board_tbl
    SET name = #{name}
    , content = #{content}
    , update_dt = #{updateDt}
    , update_id = #{updateId}
WHERE id = #{id}

UPDATE board_tbl
SET del_flag = 'Y'
  , delete_dt = #{deleteDt}
  , delete_id = #{deleteId}
WHERE id = #{id}

UPDATE board_tbl
SET view_qty = view_qty + 1
WHERE id = #{id}

UPDATE board_tbl
SET like_qty = like_qty + 1
WHERE id = #{id}
