mapper.insert:
INSERT INTO board_comment_tbl ( comment
                      , likeQty
                      , boardId
                      , createDt
                      , createId
                      , deleteFlag)
VALUES ( #{comment}
       , 0
       , #{boardId}
       , #{createDt}
       , #{createId}
       , false)

mapper.update:
UPDATE board_comment_tbl
SET comment = #{comment}
  , boardId = #{boardId}
  , updateDt = #{updateDt}
  , updateId = #{updateId}
WHERE id = #{id}

UPDATE board_comment_tbl
SET deleteFlag = #{deleteFlag}
  , deleteDt = #{deleteDt}
  , deleteId = #{deleteId}
WHERE id = #{id}
