mapper.insert:
INSERT INTO board_like_tbl ( tbl
                           , likeUserId
                           , boardId)
VALUES ( #{tbl}
       , #{likeUserId}
       , #{boardId})

mapper.delete:
DELETE FROM board_like_tbl
WHERE tbl = #{tbl}
  AND likeUserId = #{likeUserId}
  AND boardId = #{boardId}

mapper.select:
SELECT count(*)
FROM board_like_tbl
WHERE tbl = #{tbl}
  AND likeUserId = #{likeUserId}
  AND boardId = #{boardId}
