mapper.insert:
INSERT INTO comment_like_tbl ( commentTbl
                      , createId
                      , commentId)
VALUES ( #{commentTbl}
       , #{createId}
       , #{commentId})

mapper.delete:
DELETE FROM comment_like_tbl
WHERE commentTbl = #{commentTbl}
  AND createId = #{createId}
  AND commentId = #{commentId}

mapper.select:
SELECT count(*)
FROM comment_like_tbl
WHERE commentTbl = #{commentTbl}
  AND createId = #{createId}
  AND commentId = #{commentId}
