mapper.insert:
INSERT INTO member_tbl ( name
                       , nickname
                       , loginId
                       , password
                       , email
                       , role
                       , active
                       , createDt
                       , createId )
VALUES ( #{name}
       , #{nickname}
       , #{loginId}
       , #{password}
       , #{email}
       , #{role}
       , 0
       , #{createDt}
       , #{createId} )

mapper.update:
UPDATE member_tbl
    SET name = #{name}
    , nickname = #{nickname}
    , email = #{email}
    , role = #{role}
    , active = #{active}
    , updateDt = #{updateDt}
    , updateId = #{updateId}
WHERE id = #{id}

UPDATE member_tbl
SET deleteFlag = #{deleteFlag}
  , deleteDt = #{deleteDt}
  , deleteId = #{deleteId}
WHERE id = #{id}
