mapper.insert:
        INSERT INTO sbfile_tbl
            ( name
            , ord
            , fileType
            , uniqName
            , length
            , description
            , tbl
            , boardId
            , deleteFlag
            )
        VALUES
            ( #{name}
            , #{ord}
            , #{fileType}
            , #{uniqName}
            , #{length}
            , #{description}
            , #{tbl}
            , #{boardId}
	    , 0)

mapper.select:
SELECT count(*)
FROM sbfile_tbl
WHERE tbl = #{tbl}
  AND boardId = #{boardId}
