mapper.insert:
INSERT INTO sblike_tbl ( tbl
, nickname
, boardId)
VALUES ( #{tbl}
, #{nickname}
, #{boardId})

mapper.delete:
DELETE FROM sblike_tbl
WHERE tbl = #{tbl}
AND nickname = #{nickname}
AND boardId = #{boardId}

mapper.select:
SELECT count(*)
FROM sblike_tbl
WHERE tbl = #{tbl}
AND nickname = #{nickname}
AND boardId = #{boardId}
